package forteresse;

import serveur.Serveur;

public class Rally {
	
	public static final int CARRE_DESTRUCTIBLE = 0;
	public static final int CARRE_INDESTRUCTIBLE = 1;
	public static final int CARRE_OBJECTIF = 2;
	public static final int CARRE_NONRECONSTRUCTIBLE = 3;
	public static final int CARRE_INCONSTRUCTIBLE = 4;
	public static final int CARRE_CONSTRUCTIBLE = 5;
	
	public static final long MAP_PAS_ENREGISTREE = -1l;
	private static final long MAP_RALLY_OFFICIEL = 0l;
	
	public static final int NOTE_NON_CLASSE = -1;
	public static final int MAP_EN_ATTENTE = 0;
	
	public static final int MODE_RALLY = 0;
	public static final int MODE_KILL = 1;
	public static final int MODE_FRIGO = 2;
	public static final int MODE_TRAITRE = 3;
	public static final int MODE_EXPLORATION = 4;
	public static final int MODE_AUTRE = 5;
	
	public static final int COULEUR_EQP_LIBRE = 0;
	public static final int COULEUR_EQP_ROUGE = 1;
	public static final int COULEUR_EQP_BLEU = 2;
	
	public static final int QUALITE_TRES_MOYENNE = 0;
	public static final int QUALITE_MOYENNE = 1;
	public static final int QUALITE_BONNE = 2;
	public static final int QUALITE_TRES_BONNE = 3;
	public static final int QUALITE_EXCELLENTE = 4;
	
	public static final int NIVEAU_TRES_FACILE = 0;
	public static final int NIVEAU_FACILE = 1;
	public static final int NIVEAU_MOYEN = 2;
	public static final int NIVEAU_DIFFICILE = 3;
	public static final int NIVEAU_TRES_DIFFICILE = 4;
	
	private long Id;
	private String Auteur;
	private String Titre;
	private String Code;
	public int Couleur;
	private int Difficulte;
	private int Mode;
	private int Note;
	private int Status;
	private int Record;
	private String Recordman;
	private int[] CouleursCarres;
	private MondeForteresse Monde;
	
	public Rally(long Id, MondeForteresse Monde, String Auteur, String Titre, int Couleur, int Difficulte, int Mode, int Note, String Code, int Status) {
		this.Id = Id;
		this.Monde = Monde;
		this.Auteur = Auteur;
		this.Titre = Titre;
		this.Couleur = Couleur;
		this.Difficulte = Difficulte;
		this.Mode = Mode;
		this.Note = Note;
		this.Code = Code;
		this.Status = Status;
		this.Record = 0;
		this.Recordman = "";
		
		this.CouleursCarres = null;
	}
	
	public Rally(MondeForteresse Monde, String[] Couleurs, String Code) {
		this.Id = Rally.MAP_PAS_ENREGISTREE;
		this.Monde = Monde;
		this.Code = Code;
		
		this.CouleursCarres = new int[6];
		this.CouleursCarres[CARRE_DESTRUCTIBLE] = Integer.parseInt(Couleurs[0], 16);
		this.CouleursCarres[CARRE_INDESTRUCTIBLE] = Integer.parseInt(Couleurs[1], 16);
		this.CouleursCarres[CARRE_OBJECTIF] = Integer.parseInt(Couleurs[2], 16);
		this.CouleursCarres[CARRE_NONRECONSTRUCTIBLE] = Integer.parseInt(Couleurs[3], 16);
		this.CouleursCarres[CARRE_INCONSTRUCTIBLE] = Integer.parseInt(Couleurs[4], 16);
		this.CouleursCarres[CARRE_CONSTRUCTIBLE] = Integer.parseInt(Couleurs[5], 16);
	}

	public Rally(long Id, MondeForteresse Monde, String Auteur, String Titre, int Couleur, int Difficulte, int Mode, int Note, String Code, int Status, int c1, int c2, int c3, int c4, int c5, int c6, int Record, String Recordman) {
		this.Id = Id;
		this.Monde = Monde;
		this.Auteur = Auteur;
		this.Titre = Titre;
		this.Couleur = Couleur;
		this.Difficulte = Difficulte;
		this.Mode = Mode;
		this.Note = Note;
		this.Code = Code;
		this.Status = Status;
		this.Record = Record;
		this.Recordman = Recordman;
		
		
		this.CouleursCarres = new int[6];
		this.CouleursCarres[CARRE_DESTRUCTIBLE] = c1;
		this.CouleursCarres[CARRE_INDESTRUCTIBLE] = c2;
		this.CouleursCarres[CARRE_OBJECTIF] = c3;
		this.CouleursCarres[CARRE_NONRECONSTRUCTIBLE] = c4;
		this.CouleursCarres[CARRE_INCONSTRUCTIBLE] = c5;
		this.CouleursCarres[CARRE_CONSTRUCTIBLE] = c6;
	}
	
	// Initialisation du /rally !
	public Rally() {
		Code = "";
		Monde = Serveur.getServeur().Forteresse_ListeMonde.get(7);
		Id = MAP_RALLY_OFFICIEL;
		
		CouleursCarres = new int[6];
		CouleursCarres[CARRE_DESTRUCTIBLE] = Integer.parseInt(Forteresse.COULEUR_CARRE_DESTRUCTIBLE, 16);
		CouleursCarres[CARRE_INDESTRUCTIBLE] = Integer.parseInt(Forteresse.COULEUR_CARRE_INDESTRUCTIBLE, 16);
		CouleursCarres[CARRE_OBJECTIF] = Integer.parseInt(Forteresse.COULEUR_CARRE_OBJECTIF, 16);
		CouleursCarres[CARRE_NONRECONSTRUCTIBLE] = Integer.parseInt(Forteresse.COULEUR_CARRE_NONRECONSTRUCTIBLE, 16);
		CouleursCarres[CARRE_INCONSTRUCTIBLE] = Integer.parseInt(Forteresse.COULEUR_CARRE_INCONSTRUCTIBLE, 16);
		CouleursCarres[CARRE_CONSTRUCTIBLE] = Integer.parseInt(Forteresse.COULEUR_CARRE_CONSTRUCTIBLE, 16);
	}
	
	public boolean estRallyOfficiel() {
		return (Id == MAP_RALLY_OFFICIEL);
	}
	
	public void setCouleursPartie(Forteresse partie) {
		partie.SetCouleurs(Integer.toString(CouleursCarres[0], 16), Integer.toString(CouleursCarres[1], 16), Integer.toString(CouleursCarres[2], 16)
						 , Integer.toString(CouleursCarres[3], 16), Integer.toString(CouleursCarres[4], 16), Integer.toString(CouleursCarres[5], 16));
	}
	
	public String couleurCarresToString() {
		String c = "";
		
		if (CouleursCarres == null) { // Pas de couleurs personnalisées
			return "";
		}
		
		for (int couleur : CouleursCarres) {
			c += "-" + Integer.toString(couleur, 16);
		}
		
		return c.substring(1);
	}
	
	public String getInfosMondePerso() {
		if (Monde.MondePersonnalise) {
			return Monde.getInfosMondePerso("-");
		} else {
			return null;
		}
	}
	
	public void derepertorier() {
		Id = MAP_PAS_ENREGISTREE;
	}

	
	public boolean estRepertoriée() {
		return (Id != MAP_PAS_ENREGISTREE);
	}
	
	public int getStatus() {
		if (estRepertoriée()) {
			return Status;
		}
		
		return MAP_EN_ATTENTE;
	}
	
	public int getRecord() {
		if (estRepertoriée()) {
			return Record;
		}
		
		return -1;
	}
	
	public int getNote() {
		if (estRepertoriée()) {
			return Note;
		}
		
		return NOTE_NON_CLASSE;
	}
	
	public String getTitre() {
		if (estRepertoriée()) {
			return Titre;
		}
		
		return "?";
	}
	
	public String getAuteur() {
		if (estRepertoriée()) {
			return Auteur;
		}
		
		return "?";
	}
	
	public String getRecordman() {
		if (estRepertoriée()) {
			return Recordman;
		}
		
		return "?";
	}
	
	public int getMode() {
		if (estRepertoriée()) {
			return Mode;
		}
		
		return Forteresse.MODE_AUCUN;
	}
	
	public int getDifficulte() {
		return Difficulte;
	}
	
	public String getMap() {
		return Code;
	}
	
	public MondeForteresse getMonde() {
		return Monde;
	}
	
	public long getID() {
		return Id;
	}
}
