	private void _Initialisation_SanctionsClassementsTimers() {
		try {
			if (!LOCAL) {
				Bdd.Requete_SupprimerAnciensMP.execute();
				serveur.BOITE.Requete(Boite.LOADBANDEF);
				serveur.BOITE.Requete(Boite.LOADSANCTIONS);
			}
			serveur.BOITE.Requete(Boite.LOAD_FUSIONS);
			serveur.BOITE.Requete(Boite.DELETE_RALLYS);
			
			Elo.initClassementElo();

			MAJ_Classement();
			
			// Suppression des comptes inutilisés tous les jours
			timerSupprComptesInactifs = new Timer();
			timerTaskSupprCmptInact = new TimerTask() {
				  			public void run() {
				  				serveur.BOITE.Requete(Boite.DELETE_COMPTES_INUTILISES);
				  			}
						  };

			timerSupprComptesInactifs.schedule(timerTaskSupprCmptInact, 60 * 60 * 1000, 24 * 60 * 60 * 1000);
			//
		} catch (Exception e) {
			System.err.print("3XC _Initialisation_Sanctions ");
			e.printStackTrace();
		}
	}
	
	private void _Initialisation_BDD() {
		//
		BOITE = new Boite(this);
		//
		
		// Mal de tête
		CodeMalDeTête = "";
		ArrayList<Integer> Liste = new ArrayList<Integer>();
		for (int i = 0; i < 10; i++) {
			Liste.add(i);
		}
		for (int i = 0; i < 10; i++) {
			int Chiffre = Liste.remove((int) (Math.random() * Liste.size()));
			if (Chiffre == 0) {
				MalDeTête[i] = String.valueOf((char) 10);
				Aspirine[10] = i;
			} else {
				MalDeTête[i] = String.valueOf((char) Chiffre);
				Aspirine[Chiffre] = i;
			}
			CodeMalDeTête += Chiffre;
		}
	}
	
	public void StopSupprAutoComptesInut() {
		timerSupprComptesInactifs.cancel();
		timerTaskSupprCmptInact.cancel();
	}

