	public void Reload_Sanctions() {
		try {
			ResultSet Resultat = Bdd.Requete_ReloadSanctions.executeQuery();
			System.err.println("");
			while (Resultat.next()) {
				String NomCible = Resultat.getString("nom_joueur");
				String Sanction = Resultat.getString("sanction");
				String IP = Resultat.getString("ip");
				int TempsSanction = Resultat.getInt("temps");
				Long Fin = Resultat.getLong("fin");
				Long Temps = 1000 * Fin - System.currentTimeMillis();
				if (Temps > 1000) {
					int Type;
					String TypeSanction;
					if (Sanction.equals("banni")) {
						Type = TIMER_BAN;
						TypeSanction = "BAN";
						if (IP != null && !IP.isEmpty() && !ListeBanniIP.contains(IP)) {
							ListeBanniIP.add(IP);
						}
						if (NomCible != null && !NomCible.isEmpty() && !NomCible.startsWith("*") 
								&& !ListeBanniNom.contains(NomCible.toLowerCase())) {
							ListeBanniNom.add(NomCible.toLowerCase());
						}
					} else
						if (Sanction.equals("mute")) {
							Type = TIMER_MUTE;
							TypeSanction = "MUTE";
							if (IP != null && !IP.isEmpty() && !ListeMuetIP.contains(IP)) {
								ListeMuetIP.add(IP);
							}
							if (NomCible != null && !NomCible.isEmpty() && !ListeMuetNom.contains(NomCible.toLowerCase())) {
								ListeMuetNom.add(NomCible.toLowerCase());
							}
						} else
							if (Sanction.equals("mute_forum")) {
								Type = TIMER_MUTE_FORUM;
								TypeSanction = "MUTE_FORUM";
								if (IP != null && !IP.isEmpty() && !ListeMuetForum.contains(IP)) {
									ListeMuetForum.add(IP);
								}

							} else
								if (Sanction.equals("mute_forum_nom")) {
									Type = TIMER_MUTE_FORUM_NOM;
									TypeSanction = "MUTE_FORUM_NOM";
									if (NomCible != null && !NomCible.isEmpty() && 
											!ListeMuetForumNom.contains(NomCible.toLowerCase())) {
										ListeMuetForumNom.add(NomCible.toLowerCase());
									}
								} else {
									continue;
								}

					System.err.println("Reban auto " + IP + " " + (Temps / 3600000) + "/" + TempsSanction + " " 
											+ TypeSanction + " " + NomCible);
					if (Temps < 60 * 24 * 3600000) { // on n'envisage pas le déban s'il reste plus de 2 mois
						new TimerBan(IP, Temps, Type, NomCible.toLowerCase());
					}
				}

			}
			System.out.println("\nBan ip reloadés : " + serveur.ListeBanniIP.size() 
					+ "\n" + "Ban nom reloadés : " + serveur.ListeBanniNom.size() 
					+ "\n" + "Mute ip reloadés : " + serveur.ListeMuetIP.size() 
					+ "\n" + "Mute nom reloadés : " + serveur.ListeMuetNom.size() 
					+ "\n" + "Mute forum ip reloadés : " + serveur.ListeMuetForum.size() 
					+ "\n" + "Mute forum nom reloadés : " + serveur.ListeMuetForumNom.size() 
					+ "\n");
		} catch (Exception e) {
			System.err.print("3XC Reload_Sanctions ");
			e.printStackTrace();
		}

	}

