	public boolean Refus(Joueur Joueur) {

		Iterator<String> it;
		if (serveur.FiltrageHost) { // système activé
			it = ListeRefusHost.iterator();
			while (it.hasNext()) {
				String o = it.next();
				if (Joueur.AdresseHost.contains(o)) {
					return true;
				}
			}
		}

		it = ListeRefusIP.iterator();
		while (it.hasNext()) {
			String o = (String) it.next();
			if (Joueur.AdresseIP.startsWith(o)) {
				return true;
			}
		}
		return false;

	}
	
