	public void Message_Team(Joueur JOUEUR, String MESSAGE, int ID_TEAM, boolean META) {
		int Nb = ListeJoueur.size();
		ArrayList<Joueur> Liste = new ArrayList<Joueur>();
		for (int i = 0; i < Nb; i++) {
			Joueur Joueur = ListeJoueur.get(i);
			if (Joueur.Membre && 
					(Joueur.IdTeam == ID_TEAM || 
					(serveur.ListeTeamAmie.get(ID_TEAM) != null && serveur.ListeTeamAmie.get(ID_TEAM).contains(Joueur.IdTeam) &&
					(!Joueur.IgnorerTouteFusion && !Joueur.IgnorerFusion.contains(ID_TEAM)) /* Joueur n'ignore pas la fusion */
					&& (!JOUEUR.IgnorerTouteFusion && !JOUEUR.IgnorerFusion.contains(Joueur.IdTeam)))) /* team pas ignorée par l'émetteur du message */
					&& Joueur.AccepteCanalTeam) {
				if (!META || !Joueur.NomJoueur.equalsIgnoreCase(JOUEUR.NomJoueur)) {
					boolean EnvoieJoueur = true;
					if (Joueur.ListeJoueursIgnorés != null && Joueur.ListeJoueursIgnorés.contains(JOUEUR.NomJoueur)) {
						EnvoieJoueur = false;
					}
					if (EnvoieJoueur) {
						Liste.add(Joueur);
					}

				}
			}
		}
		Envoie(Liste, "TxT#[" + Liste.size() + "]" + (!META ? "[" + JOUEUR.NomJoueur + "] " : " ") + MESSAGE, false);

	}
	
