	public void Affichage_Liste_Arbitre(Joueur JOUEUR) {
		StringBuilder LsArb = new StringBuilder();
		StringBuilder LsModo = new StringBuilder();
		StringBuilder LsModoF = new StringBuilder();
		StringBuilder LsDev = new StringBuilder();

		int Num = serveur.ListeJoueur.size();
		for (int i = 0; i < Num; i++) {
			Joueur Joueur = serveur.ListeJoueur.get(i);

			if (Joueur.AutorisationDev) {
				LsDev.append(", " + Joueur.NomJoueur);
			} else if (Joueur.AutorisationArbitre || Joueur.AutorisationSuperArbitre) {
				LsArb.append(", " + Joueur.NomJoueur);
			} else if (Joueur.AutorisationModoPublic) {
				LsModo.append(", " + Joueur.NomJoueur);
			} else if (Joueur.AutorisationModoForum && !Joueur.AutorisationModo) {
				LsModoF.append(", " + Joueur.NomJoueur);
			}

		}

		//JOUEUR.MessageBoite.add("CxMSI#VIP : " + LsArb.substring(2));
		String envoie = "CxMSI#";
		
		if (LsModo.length() > 0) {
			envoie += "Modo public : " + LsModo.substring(2) + "\n[MP] ";
		}
		
		if (LsModoF.length() > 0) {
			envoie += "Modo forum : " + LsModoF.substring(2) + "\n[MP] ";
		}
		
		if (LsDev.length() > 0) {
			envoie += "Dev : " + LsDev.substring(2) + "\n[MP] ";
		}
		
		if (LsArb.length() > 0) {
			envoie += "Arbitre : " + LsArb.substring(2) + "\n[MP] ";
		}
		
		if (!envoie.equals("CxMSI#")) {
			JOUEUR.MessageBoite.add(envoie.substring(0, envoie.length() - 6));
		}
	}
	
