	public void Mute_Joueur(Joueur MODO, String JOUEUR, long TEMPS, String ARBITRE, String RAISON, boolean SILENCIEUX) {
		int Nb = ListeJoueur.size();
		for (int i = 0; i < Nb; i++) {
			Joueur Joueur = ListeJoueur.get(i);
			if (Joueur.NomJoueur.equalsIgnoreCase(JOUEUR)) {

				// pas de mute pour les arbitres sur le chat principal
				if (ARBITRE.equals("1") && Joueur.PartieEnCours == null) {
					return;
				}

				Message_Modo(MODO.NomJoueur + " vient de rendre " + (SILENCIEUX ? "MUET " : "muet ") + JOUEUR + " " + (int) (TEMPS / 3600000) + " heures. " + (RAISON.equals("") ? "" : "Raison : " + RAISON), false);
				if(MODO.AutorisationArbitre)
			      MODO.Envoie("CxINFO#Mute effectué.");

				if (TEMPS == 0) {
					// démute
					ListeMuetIP.remove(Joueur.AdresseIP);
					
					if (Joueur.AutorisationInscription) {
						ListeMuetNom.remove(Joueur.NomJoueur.toLowerCase());
					}
					Joueur.Muet = false;
					return;
				}

				ListeMuetIP.add(Joueur.AdresseIP);
				
				if (Joueur.AutorisationInscription) {
					Joueur.AutorisationForum = false;
					String Nom = Joueur.NomJoueur.toLowerCase();
					ListeMuetNom.add(Nom);
					new TimerBan(Joueur.AdresseIP, TEMPS, TIMER_MUTE, Nom);
				} else {
					new TimerBan(Joueur.AdresseIP, TEMPS, TIMER_MUTE, null);
				}
				Joueur.Muet = true;
				//
				int NbJoueur = ListeClient.size();
				for (int z = 0; z < NbJoueur; z++) {
					Joueur AutreJoueur = ListeClient.get(z);
					if (AutreJoueur.AdresseIP.equals(Joueur.AdresseIP) && AutreJoueur != Joueur) {
						AutreJoueur.Muet = true;
					}
				}
				
				if (WebSocketVisibles()) {
					ArrayList<Joueur> lsJoueurs = serveurWS.getLsClients();
					NbJoueur = lsJoueurs.size();
					for (int z = 0; z < NbJoueur; z++) {
						Joueur AutreJoueur = lsJoueurs.get(z);
						if (AutreJoueur.AdresseIP.equals(Joueur.AdresseIP) && AutreJoueur != Joueur) {
							AutreJoueur.Muet = true;
						}
					}
				}
				
				//
				Joueur.Mara_TempsBan += TEMPS / 3600000;

				String EnvoiMute = "CxMUTE#0#" + Joueur.NomJoueur + "#" + (TEMPS / 3600000) + "#" + ARBITRE + "#" + RAISON;
				/*if (MODO != null && !MODO.AutorisationAdmin && Joueur.PartieEnCours != null && Joueur.PartieEnCours.ListeJoueurPartie.size() > 13) {
					SILENCIEUX = false; // ban silencieux non autorisé en gros salons
				}*/
				if (SILENCIEUX) { // on ne notifie que le muté
					ArrayList<Joueur> ListeJoueurCible = new ArrayList<Joueur>();
					ListeJoueurCible.add(Joueur);
					serveur.Envoie(ListeJoueurCible, EnvoiMute);
				} else{
					if (Joueur.PartieEnCours == null) {
						serveur.EnvoieCP(EnvoiMute);
					} else {
						serveur.Envoie(Joueur.PartieEnCours.getLsJoueurPartie(), EnvoiMute);
					}
				}
				serveur.BOITE.Requete(Boite.LOG_SANCTION, MODO, JOUEUR, Joueur.AdresseIP, RAISON, "mute", (Long.valueOf((TEMPS / 3600000))).intValue());
				return;
			}
		}

	}
	
