	public String ExtraireHost(String AdresseHost) {
		if (AdresseHost == null || AdresseHost.isEmpty()) {
			return "";
		}

		try {
			if (AdresseHost.endsWith(ConstWanadoo)) {
				Matcher matcher = PatternWanadoo.matcher(AdresseHost);
				if (matcher.find()) {
					return matcher.group() + ConstWanadoo;
				}
			} else
				if (AdresseHost.endsWith(ConstSFR)) {
					Matcher matcher = PatternSFR.matcher(AdresseHost);
					return matcher.replaceAll("");
				} else
					if (AdresseHost.endsWith(ConstProxad)) {
						Matcher matcher = PatternProxad.matcher(AdresseHost);
						if (matcher.find()) {
							return matcher.group() + ConstProxad;
						}
					} else
						if (AdresseHost.endsWith(ConstClubInternet)) {
							Matcher matcher = PatternClubInternet.matcher(AdresseHost);
							if (matcher.find()) {
								return matcher.group() + ConstClubInternet;
							}
						} else
							if (AdresseHost.endsWith(ConstNumericable)) {
								if (!AdresseHost.startsWith("ip-")) {
									Matcher matcher = PatternNumericable.matcher(AdresseHost);
									return matcher.replaceAll("");
								}
							} else
								if (PatternIP.matcher(AdresseHost).find()) {
									Matcher matcher = PatternDébutIP.matcher(AdresseHost);
									if (matcher.find()) {
										return matcher.group();
									}
								}
			return PatternNumeral.matcher(AdresseHost).replaceAll("");

		} catch (Exception e) {
			System.err.print("3XC ExtraireHost " + AdresseHost);
			e.printStackTrace();
		}
		return "";
	}

