	public void Rejoindre_Liste_Partie(Joueur JOUEUR) {
		
			if (JOUEUR.PartieEnCours != null) {
				JOUEUR.PartieEnCours.Quitter_partie(JOUEUR);
			}
			
			if (JOUEUR.PartieEnAttente != null && JOUEUR.PartieEnAttente.AttenteElo) {
				JOUEUR.PartieEnAttente.AttenteEloPartie.Joueur_Quitte_Attente_Elo(JOUEUR, true);
			}

			JOUEUR.ReceptionBouboum = false;
			JOUEUR.ReceptionAaaah = false;
			JOUEUR.ReceptionForteresse = false;
			
			//
			StringBuilder Info = new StringBuilder("CxJ#0#");
			if (!JOUEUR.SansPartie) {
				EnvoieCP("CxO#" + JOUEUR.NomJoueur + (Evénement.Saturnaaaahles() ? "#" + (JOUEUR.ParticipeSurvivalKick ? "1" : "0") : ""));
				ListeJoueurSansPartie.add(JOUEUR);
				JOUEUR.SansPartie = true;
			}
			int Nb = ListeJoueurSansPartie.size();
			for (int i = 0; i < Nb; i++) {
				Joueur Joueur = ListeJoueurSansPartie.get(i);
				int CodeSurLeForum = Joueur.SurLeForum ? 1 : 0;
				Info.append(";" + Joueur.NomJoueur + "," + CodeSurLeForum);
				if (Evénement.Saturnaaaahles()) {
					Info.append(",");
					Info.append(Joueur.ParticipeSurvivalKick ? "1" : "0");
				}
			}
			//
			int NbPartie = ListePartie.size();
			for (int i = 0; i < NbPartie; i++) {
				Partie Partie = ListePartie.get(i);
				if (!Partie.EditeurAaaah) {
					int NbJoueur = Partie.getLsJoueurPartie().size();
					if (NbJoueur == 0 && Partie.AttenteEloPartie != null) {
						NbJoueur = Partie.AttenteEloPartie.getNbJoueur();
					}
					Info.append($ + $ + Partie.CodePartie + $ + Partie.CodeJeu + $ + Partie.NomPartie + $ + NbJoueur + $ + (Partie.hasMotDePasse() ? "1":"0") + $ + Partie.Couleur_Partie(JOUEUR) + $ + Partie.TypePartie());
				}
			}
			//
			JOUEUR.Envoie(Info.toString());
			// Première connexion
			if (!JOUEUR.EntièrementConnecté) {
				JOUEUR.EntièrementConnecté = true;
				Joueur_Entièrement_Connecté(JOUEUR);
			}
		
	}

