	public void Rejoindre_Partie(Joueur JOUEUR, int CODE_JEU, String NOM_PARTIE, String MOT_DE_PASSE, boolean EDITEUR_AAAAH, long TEMPS_PARTIE, boolean AutoriserChoixEquipe, boolean Dessin, int SCORE_MAX_BOUM, boolean SANS_CONTA, int MAX_JOUEURS, boolean SALON_FIGHT, int MODE_AAAAH, boolean SALON_KILL, int MODE_FORTO, String ARBITRE_ELO, String GUIDE, boolean MATCH_TEAM) {
		///JOUEUR.Envoie("CxJ#" + CODE_JEU);
		//
		if (NOM_PARTIE == null) { // Salon Offi (créer une partie rapide)
			JOUEUR.Envoie("CxJ#" + CODE_JEU);
			int NumMax = 0;
			int Nb = ListePartie.size();
			for (int i = 0; i < Nb; i++) {
				Partie Partie = ListePartie.get(i);
				if (!Partie.PartiePersonnalisée && Partie.CodeJeu == CODE_JEU) {
					if (Partie.getLsJoueurPartie().size() < Partie.JoueurMaximum) {
						Partie.Nouveau_Joueur(JOUEUR, null);
						return;
					} else {
						if (NumMax <= Partie.NumPartieOfficelle) {
							NumMax = Partie.NumPartieOfficelle + 1;
						}
					}
				}
			}	
				
			String NomPartie;
			if (CODE_JEU == 1) {
				NomPartie = "Bouboum - Serveur officiel n°" + NumMax;
			} else if (CODE_JEU == 2) {
				NomPartie = "Aaaah - Serveur officiel n°" + NumMax;
			} else {
				NomPartie = "Forteresse - Serveur officiel n°" + NumMax;
			}
			
			Partie NouvellePartie = new Partie(false, CODE_JEU, NomPartie, null, false, true);
			NouvellePartie.NumPartieOfficelle = NumMax;
			NouvellePartie.Nouveau_Joueur(JOUEUR, null);
		} else {
			Partie NouvellePartie = new Partie(true, CODE_JEU, NOM_PARTIE, MOT_DE_PASSE, EDITEUR_AAAAH, TEMPS_PARTIE, AutoriserChoixEquipe, Dessin, SCORE_MAX_BOUM, SANS_CONTA, MAX_JOUEURS, SALON_FIGHT, MODE_AAAAH, SALON_KILL, false, MODE_FORTO, ARBITRE_ELO, GUIDE, MATCH_TEAM);
			NouvellePartie.Nouveau_Joueur(JOUEUR, null);
		}
	}	

	public int Code_Joueur_Forteresse(Joueur JOUEUR, Forteresse SALON) {
		int CodeJoueur = 0;
		while (true) {
			boolean CodeValide = true;
			int Nb = SALON.ListeJoueur.size();
			for (int i = 0; i < Nb; i++) {
				Joueur Joueur = SALON.ListeJoueur.get(i);
				if (Joueur._Forteresse.CodeJoueur == CodeJoueur) {
					CodeValide = false;
					break;
				}
			}
			if (CodeValide) {
				return CodeJoueur;
			}
			CodeJoueur++;
		}
	}

