	public void Envoie_Chat(Joueur JOUEUR, ArrayList<Joueur> LS_JOUEUR, String MESSAGE) {
		int Nb = LS_JOUEUR.size();
		if ((Calendar.getInstance().getTimeInMillis() / 1000) - JOUEUR.TempsMuet < 0 && !JOUEUR.AutorisationModo && !JOUEUR.AutorisationModoForum) {
			if (Nb > 1 || (Nb == 1 && LS_JOUEUR.get(0) == JOUEUR)) {
				JOUEUR.Envoie(MESSAGE);
			}
			return;
		}
		for (int j = 0; j < Nb; j++) {
			Joueur Joueur = LS_JOUEUR.get(j);
			boolean EnvoieJoueur = true;
			if (Joueur.ListeJoueursIgnorés.contains(JOUEUR.NomJoueur) || (Joueur.ListeIPIgnorées.contains(JOUEUR.AdresseIP) && Nb == 1)) {
				// le joueur JOUEUR est dans la liste noire de Joueur
				if (!Joueur.AutorisationModo || Nb == 1) { // les modos voient les joueurs ignorés sur le chat
					if (!(Joueur.PartieEnCours == null && JOUEUR.PartieEnCours == null && JOUEUR.NomModifié)) {
						EnvoieJoueur = false; // le joueur est ignoré
					}
				}
			}

			if (EnvoieJoueur) {
				Joueur.Envoie(MESSAGE);
			} else {
				if (Nb == 1) {
					// on chuchote à un joueur qui nous ignore
					JOUEUR.Envoie("CxIGN#" + Joueur.NomJoueur);
				}
			}
		}
	}

	public String IO(String CHEMIN, String TEXTE) {
		if (TEXTE != null) {
			PrintWriter Ecriture = null;
			try {
				Ecriture = new PrintWriter(new FileOutputStream(CHEMIN));
				Ecriture.print(TEXTE);
			} catch (Exception e) {
				e.printStackTrace();
				return null;
			} finally {
				try {
					Ecriture.close();
				} catch (Exception e) {
				}
			}
			return "";
		} else {
			BufferedReader br = null;
			StringBuilder Texte = new StringBuilder();
			try {
				InputStream ips = new FileInputStream(CHEMIN);
				InputStreamReader ipsr = new InputStreamReader(ips);
				br = new BufferedReader(ipsr);
				String ligne;
				while ((ligne = br.readLine()) != null) {
					Texte.append(ligne);
				}
				br.close();
				return Texte.toString();
			} catch (Exception e) {
				e.printStackTrace();
				return null;
			} finally {
				try {
					br.close();
				} catch (Exception e) {
				}
			}
		}
	}

	class TimerBan extends Thread {

		private long Temps;
		private int Type;
		private String IP;
		private String Nom;
		private boolean AvecNom;

		public TimerBan(String _IP, long TEMPS, int TYPE, String NOM) {
			NbThread++;
			if (NOM == null || NOM.isEmpty()) {
				AvecNom = false;
			} else {
				AvecNom = true;
				Nom = NOM;
			}
			Type = TYPE;
			IP = _IP;
			Temps = TEMPS;
			if (TEMPS == 4813200000L)
				Temps = 30000; // pour les tests 1337
			this.start();
		}

		public void run() {
			try {
				sleep(Temps);
				if (Type == TIMER_BAN) {
					ListeBanniIP.remove(IP);
					if (AvecNom) {
						ListeBanniNom.remove(Nom);
					}
				} else
					if (Type == TIMER_MUTE) {
						ListeMuetIP.remove(IP);
						if (AvecNom) {
							ListeMuetNom.remove(Nom);
							Joueur Cible = Joueur(Nom);
							if (Cible != null) {
								Cible.Muet = false;
							}
						}
					} else
						if (Type == TIMER_MUTE_FORUM) {
							Demute_Forum(IP);
						} else
							if (Type == TIMER_MUTE_FORUM_NOM) {
								Demute_Forum_Nom(Nom);
							}
				NbThread--;
			} catch (Exception e) {
				System.err.print("3XC TimerBan ");
				e.printStackTrace();
			}
		}
	}
	
	public void Retirer_Team_Fusion(int IdTeam, Joueur Par) {
		if (serveur.ListeTeamAmie.containsKey(IdTeam)) {
			FusionTeam Fusion = serveur.ListeTeamAmie.get(IdTeam);
			// on notifie les joueurs de la fusion
			if (Par != null) {
				serveur.Message_Team(Par, "La team " + IdTeam + " vient de quitter cette fusion.", IdTeam, true);
				Par.Envoie("CxINFO#Fusion dissoute.");
			}
			
			// la team du joueur n'est plus dans la fusion
			Fusion.remove(IdTeam); 
			// si la fusion ne comporte plus qu'un membre, on la dissout
			if (Fusion.Alliance.size() <= 1) {							
				serveur.ListeTeamAmie.remove(Fusion.Alliance.get(0));
				serveur.BOITE.Requete(Boite.SUPPRIMER_FUSION, Par, Fusion.Id);
			} else {
				serveur.BOITE.Requete(Boite.QUITTER_FUSION, Par, Fusion.Id, IdTeam);				
			}
			// la team n'a plus de fusion
			serveur.ListeTeamAmie.remove(IdTeam);	
		}
	}
	
