	public class Action {

		public boolean Active = true;
		public long Activation;
		public boolean Placée = false;
		public boolean BombeBouboum = false;
		public bouboum.Bombe Bombe;
		public boolean ForteresseJambombe = false;
		public Jambombe Jambombe;
		public boolean Contamination = false;
		public boolean PreContamination = false;
		public boolean NouvellePartieBouboum = false;
		public boolean NouvellePartieAaaah = false;
		public boolean NouvellePartieForteresse = false;
		public Bouboum PartieBouboum;
		public Aaaah PartieAaaah;
		public Forteresse PartieForteresse;
		private boolean Ban = false;
		public boolean RefusConnexion = false;
		public Joueur JoueurCible;
		public boolean RespawnForteresse = false;

		public Action(long ACTIVATION) {
			Temporisation(ACTIVATION);
		}

		public void Temporisation(long ACTIVATION) {
			Activation = ACTIVATION;
			if (Placée) {
				ListeAction.remove(this);
			}
			Placée = true;
			int NbAction = ListeAction.size();
			if (NbAction == 0) {
				ListeAction.add(this);
			} else {
				Action Act = ListeAction.get(NbAction - 1);
				if (Act != null && Act.Activation < Activation) {
					ListeAction.add(this);
				} else {
					for (int i = 0; i < NbAction; i++) {
						Action Action = ListeAction.get(i);
						if (Action != null && Action.Activation > Activation) {
							ListeAction.add(i, this);
							break;
						}
					}
				}
			}
		}

		public void Déclenchement() {
			try {
				if (Active) {
					//String NomAction = "Action inconnue";
					if (BombeBouboum) {
						Bombe.Boum();
						Bombe = null;
						//NomAction = "BombeBouboum";
					}
					if (ForteresseJambombe) {
						Jambombe.Boum();
						Jambombe = null;
						//NomAction = "ForteresseJambombe";
					}
					if (NouvellePartieBouboum) {
						PartieBouboum.Nouvelle_Partie(null, null);
						//NomAction = "NouvellePartieBouboum";
					}
					if (NouvellePartieAaaah) {
						PartieAaaah.Nouvelle_Partie(PartieAaaah.ProchainMonde);
						//NomAction = "NouvellePartieAaaah";
					}
					if (NouvellePartieForteresse) {
						PartieForteresse.Nouvelle_Partie(PartieForteresse.ProchainMonde, null, false);
						//NomAction = "NouvellePartieForteresse";
					}
					if (PreContamination) {
						PartieAaaah.PreContamination();
						//NomAction = "PreContamination";
					}
					if (Contamination) {
						PartieAaaah.Contamination(PartieAaaah.FuturContamine);
						//NomAction = "Contamination";
					}
					if (RespawnForteresse) {
						JoueurCible._Forteresse.Respawn();
						//NomAction = "RespawnForteresse";
					}
					if (Ban) {
						String IP = JoueurCible.AdresseIP;
						Temps_Ban(JoueurCible);
						// on déconnecte les personnes avec la même IP  
						int NbJoueur = ListeClient.size();
						for (int z = 0; z < NbJoueur; z++) {
							Joueur AutreJoueur = ListeClient.get(z);
							if (AutreJoueur.AdresseIP.equals(IP)) {
								AutreJoueur.Deconnexion("KICK IP BANNIE");
								serveur.BOITE.Requete(Boite.LOG_SANCTION, null, AutreJoueur.NomJoueur, AutreJoueur.AdresseIP, null, "kick-ip-bannie", 0);
								z--;
								NbJoueur--;
							}
						}
						
						if (WebSocketVisibles()) {
							ArrayList<Joueur> lsJoueurs = serveurWS.getLsClients();
							NbJoueur = lsJoueurs.size();
							for (int z = 0; z < NbJoueur; z++) {
								Joueur AutreJoueur = lsJoueurs.get(z);
								if (AutreJoueur.AdresseIP.equals(IP)) {
									AutreJoueur.Deconnexion("KICK IP BANNIE");
									serveur.BOITE.Requete(Boite.LOG_SANCTION, null, AutreJoueur.NomJoueur, AutreJoueur.AdresseIP, null, "kick-ip-bannie", 0);
									z--;
									NbJoueur--;
								}
							}
						}
						
						//NomAction = "Ban";
					}
					if (RefusConnexion) {
						JoueurCible.Deconnexion(JoueurCible.RaisonDeco);
						//NomAction = "RefusConnexion";
					}
				}
			} catch (Exception e) {
				System.err.print("3XC Déclenchement ");
				e.printStackTrace();
			}
		}
	}
	
	
