	public boolean SontAmoureux(Joueur J1, Joueur J2) {
		return !J1.Amoureux.isEmpty() && J1.NomJoueur.equals(J2.Amoureux) && J1.Amoureux.equals(J2.NomJoueur);
	}
	
	public Joueur TrouverAmoureux(ArrayList<Joueur> Ls, Joueur J) {
		if (J.Amoureux.isEmpty()) {
			return null;
		}
		
		for(Joueur Cible : Ls) {
			if (SontAmoureux(J, Cible)) {
				return Cible;
			}
		}
		return null;
	}

